    //}
    removeEntry(i) {
        this.listOfRoasters.splice(i, 1);
        this.saveEnable = true;
        this.findNoOfShifts();
    }
    getSequence() { return this.sequence += this.sequence + 1; }
    ;
    downSequence() { return this.sequence += this.sequence - 1; }
    ;
    reset() {
        this.form.controls['employeeNumber'].setValue('');
        this.form.controls['employeeName'].setValue('');
    }
    goBack() {
        window.history.back();
