export class OpmvendorsummaryComponent extends ParentFinMgtComponent {
  constructor(fb, apiService, authService, utilService, pageService, notifyService) {
    super(authService);
    this.fb = fb;
    this.apiService = apiService;
    this.authService = authService;
    this.utilService = utilService;
    this.pageService = pageService;
    this.notifyService = notifyService;
    this.totalItemsCount = 0;
    this.vendCode = '';
    this.isAllCustomers = false;
    this.dateFrom = '';
    this.dateTo = '';
    this.type = 'All';
    this.vendorList = [];
    this.codeControl = new FormControl('');
    this.totalBalance = 0;
    this.companyName = '';
    this.companyAddress = '';
    this.branchName = '';
    this.isLoading = false;
    this.isCodeLoading = false;
    this.filteredOptions = this.codeControl.valueChanges.pipe(startWith(''), debounceTime(400), distinctUntilChanged(), switchMap(val => {
      if (val.trim() !== '') this.isCodeLoading = true;
      return this.filter(val || '');
    }));
  }

  ngOnInit() {
    this.setForm();
  }

  setForm() {
    //let cDate: IMyDateModel = { isRange: false, singleDate: {}, dateRange: null };
    this.form = this.fb.group({
      'dateFrom': [''],
      'dateTo': [''],
      'isAllBranches': [false]
    });
  }

  filter(val) {
    return this.apiService.getall(`vendor/getVendorSelectItemList?search=${val.trim()}`).pipe(map(response => {
      const res = response; //if (res && res.length == 0)
      //  this.notifyService.showError("enter branch name")

      this.isCodeLoading = false;
      return res;
    }));
  }

  onPageSwitch(event) {
    this.search(event.pageIndex, event.pageSize);
  }

  search(page = this.pageService.page, pageCount = this.pageService.pageCount) {
    this.isLoading = true;
    this.apiService.getall(`report/getOpmVendorSummaryList?page=${page}&pageCount=${pageCount}&isAllCustomers=${this.isAllCustomers}&type=${this.type}&custCode=${this.codeControl.value}&from=${this.utilService.getCommonDate(this.dateFrom)}&to=${this.utilService.getCommonDate(this.dateTo)}`).subscribe(res => {
      this.isLoading = false;

      if (res) {
        const listItem = res['list'];
        this.vendorList = listItem['list'];
        this.totalItemsCount = listItem['reportCount'];
        this.companyName = res['comapnyName'];
        this.companyAddress = res['address'];
        this.branchName = res['branchName'];
        this.logoURL = res['logoURL'];
        this.totalBalance = res['totalBalance'];
      }
    });
  }

  openPrint() {
    const printContent = document.getElementById("printcontainer");
    this.utilService.printForLocale(printContent);
  }

}

