export default class Validation {
  static isCodeExist(controlName, isExistControl) {
    return controls => {
      let control = controls.get(controlName);
      let control2 = controls.get(isExistControl);

      if (control.value == '') {
        control.setErrors({
          required: true
        });
        return {
          required: true
        };
      } else if (control2.value) {
        control.setErrors({
          isExist: true
        });
        return {
          isExist: true
        };
      } else {
        return null;
      }
    };
  }

}
