export class CreateUpdateTransferWithReplacementComponent extends ParentOptMgtComponent {
  constructor(datepipe, translate, fb, authService, utilService, apiService, dialogRef) {
    super(authService);
    this.datepipe = datepipe;
    this.translate = translate;
    this.fb = fb;
    this.authService = authService;
    this.utilService = utilService;
    this.apiService = apiService;
    this.dialogRef = dialogRef;
    this.id = 0;
    this.readonly = "";
    this.srcSiteCodeList = [];
    this.destSiteCodeList = [];
    this.srcCustomerCode = '';
    this.destCustomerCode = '';
    this.srcEmpNumber = '';
    this.destEmpNumber = '';
    this.isDataLoading = false;
    this.srcCustCodeControl = new FormControl('', Validators.required);
    this.destCustCodeControl = new FormControl('', Validators.required);
    this.srcEmpNumberControl = new FormControl('', Validators.required);
    this.destEmpNumberControl = new FormControl('', Validators.required);
    this.isArabic = false;
    this.srcProjectsList = [];
    this.destProjectsList = [];
    this.isFromProjectsAction = false;
    this.filteredSrcCustCodes = this.srcCustCodeControl.valueChanges.pipe(startWith(this.srcCustomerCode), debounceTime(utilService.autoDelay()), distinctUntilChanged(), switchMap(val => {
      if (val.trim() !== '') this.isDataLoading = true;
      return this.filterSrcCustCodes(val || '');
    }));
    this.filteredDestCustCodes = this.destCustCodeControl.valueChanges.pipe(startWith(this.destCustomerCode), debounceTime(utilService.autoDelay()), distinctUntilChanged(), switchMap(val => {
      if (val.trim() !== '') this.isDataLoading = true;
      return this.filterDestCustCodes(val || '');
    }));
  }

  filterSrcCustCodes(val) {
    return this.apiService.getall(`CustomerMaster/getSelectCustomerList?search=${val}`).pipe(map(response => {
      const res = response;
      this.isDataLoading = false;
      return res;
    }));
  }

  filterDestCustCodes(val) {
    return this.apiService.getall(`CustomerMaster/getSelectCustomerList?search=${val}`).pipe(map(response => {
      const res = response;
      this.isDataLoading = false;
      return res;
    }));
  }

  filtersrcEmpNumbers(val) {
    return this.apiService.getall(`EmployeesToProjectSite/getAutoFillEmployeeListForProjectSite/${this.form.controls['srcProjectCode'].value}/${this.form.controls['srcSiteCode'].value}/?search=${val}`).pipe(map(response => {
      const res = response;
      this.isDataLoading = false;
      return res;
    }));
  }

  filterdestEmpNumbers(val) {
    return this.apiService.getall(`EmployeesToProjectSite/getAutoFillEmployeeListForProjectSite/${this.form.controls['destProjectCode'].value}/${this.form.controls['destSiteCode'].value}/?search=${val}`).pipe(map(response => {
      const res = response;
      this.isDataLoading = false;
      return res;
    }));
  }

  ngOnInit() {
    this.isDataLoading = true;
    this.setForm();
    this.isArabic = this.utilService.isArabic();

    if (this.id > 0) {
      this.readonly = "readonly";
      this.editForm(); // this.loadSiteCodes(this.form.controls['siteCode'].value);
    } else {
      this.readonly = "";
      this.isDataLoading = false;
    }
  }

  setForm() {
    this.form = this.fb.group({
      'srcCustomerCode': ['', Validators.required],
      'destCustomerCode': ['', Validators.required],
      'srcProjectCode': ['', Validators.required],
      'destProjectCode': ['', Validators.required],
      'srcSiteCode': ['', Validators.required],
      'destSiteCode': ['', Validators.required],
      'id': [this.id, Validators.required],
      'fromDate': ['', Validators.required],
      'srcEmployeeNumber': ['', Validators.required],
      'destEmployeeNumber': ['', Validators.required]
    });

    if (this.isFromProjectsAction) {
      this.srcCustCodeControl.setValue(this.projectData.customerCode);
      this.form.controls['srcSiteCode'].setValue(this.projectData.siteCode);
      this.form.controls['srcProjectCode'].setValue(this.projectData.projectCode);
      this.form.controls['srcCustomerCode'].setValue(this.projectData.customerCode);
      this.loadProjectsList(this.projectData.customerCode, "src");
      this.loadSiteCodes(this.projectData.projectCode, "src");
      this.onSelectionSiteCode("src");
    }
  }

  onSelectionCustomerCode(event, op, origin) {
    let CustCode = '';

    if (op == 1) {
      CustCode = event.option.value;
    } else if (op == 2) {
      CustCode = event.target.value;
    } else if (op == 3) {
      CustCode = event;
    }

    this.apiService.getall('CustomerMaster/getCustomerByCustomerCode/' + CustCode).subscribe(res => {
      if (res != null) {
        if (origin == "src") {
          this.form.controls['srcCustomerCode'].setValue(CustCode);
          this.loadProjectsList(CustCode, "src");
          this.form.controls['srcSiteCode'].setValue('');
          this.form.controls['srcProjectCode'].setValue('');
          this.srcSiteCodeList = [];
          this.srcEmpNumberControl.setValue('');
          this.form.controls['srcEmployeeNumber'].setValue('');
          this.form.controls['fromDate'].setValue('');
        }

        if (origin == "dest") {
          this.form.controls['destCustomerCode'].setValue(CustCode);
          this.loadProjectsList(CustCode, "dest");
          this.form.controls['destSiteCode'].setValue('');
          this.form.controls['destProjectCode'].setValue('');
          this.form.controls['destEmployeeNumber'].setValue('');
          this.destEmpNumberControl.setValue('');
          this.destSiteCodeList = [];
        }
      }
      /*, error => {*/
      //}
      else {
        if (origin == "src") {
          this.form.controls['srcCustomerCode'].setValue('');
          this.srcCustCodeControl.setValue('');
          this.srcProjectsList = [];
          this.srcSiteCodeList = [];
          this.form.controls['srcProjectCode'].setValue('');
          this.form.controls['srcSiteCode'].setValue('');
          this.srcEmpNumberControl.setValue('');
          this.form.controls['srcEmployeeNumber'].setValue('');
          this.form.controls['fromDate'].setValue('');
        }

        if (origin == "dest") {
          this.form.controls['destCustomerCode'].setValue('');
          this.destCustCodeControl.setValue('');
          this.destProjectsList = [];
          this.destSiteCodeList = [];
          this.form.controls['destProjectCode'].setValue('');
          this.form.controls['destSiteCode'].setValue('');
          this.destEmpNumberControl.setValue('');
          this.form.controls['destEmployeeNumber'].setValue('');
        }
      }
    });
  }

  onSelectionEmployee(event, op, origin) {
    let EmpNumber = '';

    if (op == 1) {
      EmpNumber = event.option.value.toString();
    } else if (op == 2) {
      EmpNumber = event.target.value.toString();
    } else if (op == 3) {
      EmpNumber = event;
    }

    if (origin == "src") {
      this.apiService.getall('Employee/getEmployeeByEmployeeNumber/' + EmpNumber).subscribe(res => {
        if (res != null) {
          let srcEmpNumber = this.srcEmpNumberControl.value;
          this.form.controls['srcEmployeeNumber'].setValue(srcEmpNumber);
        } else {
          this.form.controls['srcEmployeeNumber'].setValue('');
        }
      });
    } else if (origin == "dest") {
      this.apiService.getall('Employee/getEmployeeByEmployeeNumber/' + EmpNumber).subscribe(res => {
        if (res != null) {
          let destEmpNumber = this.destEmpNumberControl.value;
          this.form.controls['destEmployeeNumber'].setValue(destEmpNumber);
        } else {
          this.form.controls['destEmployeeNumber'].setValue('');
        }
      });
    }
  }

  onSelectionProjectCode(origin) {
    if (origin == "src") {
      this.form.controls['srcSiteCode'].setValue('');
      this.srcSiteCodeList = [];
      this.loadSiteCodes(this.form.controls['srcProjectCode'].value, origin);
      this.form.controls['fromDate'].setValue('');
      this.srcEmpNumberControl.setValue('');
      this.form.controls['srcEmployeeNumber'].setValue('');
    } else if (origin == "dest") {
      this.form.controls['destSiteCode'].setValue('');
      this.destSiteCodeList = [];
      this.loadSiteCodes(this.form.controls['destProjectCode'].value, origin);
      this.destEmpNumberControl.setValue('');
      this.form.controls['destEmployeeNumber'].setValue('');
    }

    this.isDataLoading = false;
  }

  getProjectData(origin) {
    if (origin == "src") {
      this.apiService.getall(`ProjectSites/getProjectSiteByProjectAndSiteCode/${this.form.controls['srcProjectCode'].value}/${this.form.controls['srcSiteCode'].value}`).subscribe(res => {
        this.srcProjectData = res;
      });
    } else {
      this.apiService.getall(`ProjectSites/getProjectSiteByProjectAndSiteCode/${this.form.controls['destProjectCode'].value}/${this.form.controls['destSiteCode'].value}`).subscribe(res => {
        this.destProjectData = res;
      });
    }
  }

  loadSiteCodes(projectCode, origin) {
    this.isDataLoading = true;
