    onPageSwitch(event) {
      this.pageService.change(event);
      this.loadList(event.pageIndex, event.pageSize, this.searchValue, this.sortingOrder);
    }

    loadList(page, pageCount, query, orderBy, approval = "") {
      this.isLoading = true;
      this.apiService.getPagination('PurchaseOrder/GetPagedGRNList', this.utilService.getQueryString(page, pageCount, query, orderBy)).subscribe(result => {
        this.totalItemsCount = 0;
        this.data = new MatTableDataSource(result.items);
        this.totalItemsCount = result.totalCount;
        setTimeout(() => {
          this.paginator.pageIndex = page;
          this.paginator.length = this.totalItemsCount;
        });
        this.data.sort = this.sort; //console.log(this.data.sort)
        //console.log(this.data.paginator)

        this.isLoading = false;
      }, error => this.utilService.ShowApiErrorMessage(error));
    }

    applyFilter(searchVal) {
      const search = searchVal; //.target.value as string;
      //if (search && search.length >= 3) {

      if (search) {
        this.searchValue = search;
        this.loadList(0, this.pageService.pageCount, this.searchValue, this.sortingOrder);
      }
    }

    purchaseOrdersubmit() {
      if (this.id > 0) this.form.value['id'] = this.id;
      this.apiService.post('PurchaseOrder', this.form.value).subscribe(res => {
        debugger;

        if (res) {
          this.utilService.OkMessage();
        }
      }, error => {
        console.error(error);
        this.utilService.ShowApiErrorMessage(error);
      });
    }

    delete(id) {
      const dialogRef = this.utilService.openDeleteConfirmDialog(this.dialog, DeleteConfirmDialogComponent);
      dialogRef.afterClosed().subscribe(canDelete => {
        if (canDelete && id > 0) {
          this.apiService.delete('PurchaseOrder/GRNDelete', id).subscribe(res => {
            this.refresh();
            this.utilService.OkMessage();
          });
        }
      }, error => this.utilService.ShowApiErrorMessage(error));
    }

