export class CustomerbalancesummaryComponent extends ParentFinMgtComponent {
  constructor(fb, apiService, authService, utilService, pageService, notifyService) {
    super(authService);
    this.fb = fb;
    this.apiService = apiService;
    this.authService = authService;
    this.utilService = utilService;
    this.pageService = pageService;
    this.notifyService = notifyService;
    this.totalItemsCount = 0;
    this.vendCode = '';
    this.isSummary = false;
    this.dateFrom = '';
    this.dateTo = '';
    this.siteCode = '';
    this.isArab = false;
    this.isSiteLoading = false;
    this.customerSiteList = [];
    this.vendorSummaryList = [];
    this.vendorList = [];
    this.codeControl = new FormControl('');
    this.totalBalance = 0;
    this.companyName = '';
    this.companyAddress = '';
    this.branchName = '';
    this.isLoading = false;
    this.isCodeLoading = false;
    this.openingBalance = 0;
    this.closingBalance = 0;
    this.filteredOptions = this.codeControl.valueChanges.pipe(startWith(''), debounceTime(400), distinctUntilChanged(), switchMap(val => {
      if (val.trim() !== '') this.isCodeLoading = true;
      return this.filter(val || '');
    }));
  }

  ngOnInit() {
    this.isArab = this.utilService.isArabic();
    this.setForm();
    this.dateFrom = this.utilService.getStrtingYearDate();
    this.dateTo = this.utilService.getCurrentDate();
    this.search();
  }

  setForm() {
    //let cDate: IMyDateModel = { isRange: false, singleDate: {}, dateRange: null };
    this.form = this.fb.group({
      'dateFrom': [''],
      'dateTo': [''],
      'isSummary': [false]
    });
  }

  filter(val) {
    return this.apiService.getall(`customer/getCustomerCodeSelectList?search=${val.trim()}`).pipe(map(response => {
      const res = response; //if (res && res.length == 0)
      //  this.notifyService.showError("enter branch name")

      this.isCodeLoading = false;
      return res;
    }));
  }

  resetSiteInfo() {
    this.siteCode = '';
  }

  onPageSwitch(event) {
    this.search(event.pageIndex, event.pageSize);
  }

  search(page = this.pageService.page, pageCount = this.pageService.pageCount) {
    this.isLoading = true;
    this.apiService.getall(`report/${this.isSummary ? 'getCustomerBalanceSummaryList' : 'getCustomerBalanceDetailsList'}?page=${page}&pageCount=${pageCount}&isSummary=${this.isSummary}&custCode=${this.codeControl.value}&siteCode=${this.siteCode}&from=${this.utilService.getCommonDate(this.dateFrom)}&to=${this.utilService.getCommonDate(this.dateTo)}`).subscribe(res => {
      this.isLoading = false;

      if (res) {
        if (this.isSummary) this.vendorSummaryList = res['list'];else {
          const listItem = res['listItems'];
          this.vendorList = listItem['list'];
          this.totalItemsCount = listItem['reportCount'];
        }
        this.companyName = res['comapnyName'];
        this.companyAddress = res['address'];
        this.branchName = res['branchName'];
        this.logoURL = res['logoURL']; //if (!this.isSummary) {
        //  this.vendorList.forEach(lItems => {
        //    lItems.forEach(lItem => {
        //      if (lItem.isOpening == true) {
        //        //this.openingBalance = lItem.openingBalance;
        //        console.log(lItem.openingBalance);
        //      }
        //      else if (lItem.isClosing == true) {
        //        //this.closingBalance = lItem.closingBalance;
        //        console.log(lItem.closingBalance);
        //      }
        //    });
        //  });
        //}

        this.totalBalance = res['totalBalance'];
      }
    });
  }

  openPrint() {
    const printContent = document.getElementById("printcontainer");
    this.utilService.printForLocale(printContent); //const WindowPrt: any = window.open('', '', 'left=0,top=0,width=2000,height=1000,toolbar=0,scrollbars=0,status=0');
    //setTimeout(() => {
    //  WindowPrt.document.write(this.utilService.getPrintForLocale(printContent));
    //  WindowPrt.document.close();
    //  WindowPrt.focus();
    //  WindowPrt.print();
    //  WindowPrt.close();
    //}, 50);
  }

  customerCodeSelected(evt) {
    this.customerSiteList = [];
    this.siteCode = ''; //console.log(evt.option.value, evt.option.viewValue);

    this.apiService.getall(`customer/getCustomerSitesSelectList/0?custCode=${evt.option.value}`).subscribe(res => {
      if (res) {
        this.customerSiteList = res;
        this.customerSiteList.map(i => {
          i.text = !this.isArab ? i.text : i.textTwo;
          return i;
        });
      }
    });
  }

}

