      let ulList = document.getElementsByClassName(className);
      let expan = document.getElementsByClassName(className + '_expan')[0];

      if (event.target.checked) {
        expan.textContent = '+';
        [...ulList].forEach(slide => {
          slide.style.display = 'none';
        });
      } else {
        expan.textContent = '⇩';
        [...ulList].forEach(slide => {
          slide.style.display = 'block';
        });
      }
