export class SndReportCustomerSalesComponent extends ParentSalesMgtComponent {
  constructor(fb, apiService, authService, utilService, notifyService) {
    super(authService);
    this.fb = fb;
    this.apiService = apiService;
    this.authService = authService;
    this.utilService = utilService;
    this.notifyService = notifyService;
    this.isSummary = true;
    this.isArab = false;
    this.customerSiteList = [];
    this.codeControl = new FormControl('');
    this.isLoading = false;
    this.isCodeLoading = false;
    this.type = 'All';
    this.companyName = '';
    this.companyAddress = '';
    this.branchName = '';
    this.dateFrom = '';
    this.dateTo = '';
    this.siteCode = '';
    this.summaryList = [];
    this.detailList = [];
    this.totAmount = 0;
    this.totDiscount = 0;
    this.totNetAmountBT = 0;
    this.totTaxAmount = 0;
    this.totSalesAmount = 0;
    this.totCost = 0;
    this.totGrossMargin = 0;
    this.totGrossMarginPer = 0;
    this.totCount = 0;
    this.pageNumber = 0;
    this.pageSize = 1; //No.Of Customers

    this.totalItemsCount = 0;
    this.showMargin = false;
    this.filteredOptions = this.codeControl.valueChanges.pipe(startWith(''), debounceTime(400), distinctUntilChanged(), switchMap(val => {
      if (val.trim() !== '') this.isCodeLoading = true;
      return this.filter(val || '');
    }));
  }

  ngOnInit() {
    this.isArab = this.utilService.isArabic();
    let today = new Date();
    today.setDate(today.getDate() - 7);
    this.dateFrom = today.toISOString(); // this.dateTo = this.utilService.getCurrentDate();

    this.dateTo = new Date().toISOString();
    this.search();
  }

  filter(val) {
    return this.apiService.getall(`customer/getCustomerCodeSelectList?search=${val.trim()}`).pipe(map(response => {
      const res = response;
      this.isCodeLoading = false;
      return res;
    }));
  }

  clearReport() {
    this.summaryList = [];
    this.detailList = [];
    this.totAmount = 0;
    this.totDiscount = 0;
    this.totNetAmountBT = 0;
    this.totSalesAmount = 0;
    this.totTaxAmount = 0;
    this.totCost = 0;
    this.totGrossMargin = 0;
    this.totGrossMarginPer = 0;
    this.totCount = 0;
    this.pageNumber = 0;
  }

  search() {
    this.isLoading = true; //this.summaryList = [];
    //this.detailList = [];
    //this.pageNumber = 0;

    this.clearReport();
    this.apiService.getall(`sndReports/getCustomerSales?isSummary=${this.isSummary}&type=${this.type}&custCode=${this.codeControl.value}&from=${this.utilService.getCommonDate(this.dateFrom)}&to=${this.utilService.getCommonDate(this.dateTo)}&pageSize=${this.pageSize}&pageNumber=${this.pageNumber}`).subscribe(res => {
      this.totalItemsCount = res['totalItemsCount'];

      if (res['summaryReport'].length == 0) {
        this.notifyService.showInfo("No Data Found");
        this.isLoading = false;
      } else {
        this.companyName = res['comapnyName'];
        this.companyAddress = res['address'];
        this.branchName = res['branchName'];
        this.logoURL = res['logoURL'];
        this.totAmount = res['totAmount'];
        this.totDiscount = res['totDiscount'];
        this.totNetAmountBT = res['totNetAmountBT'];
        this.totSalesAmount = res['totSalesAmount'];
        this.totTaxAmount = res['totTaxAmount'];
        this.totCost = res['totCost'];
        this.totGrossMargin = res['totGrossMargin'];
        this.totGrossMarginPer = res['totGrossMarginPer'];
        this.totCount = res['totCount'];

        if (this.isSummary) {
          this.summaryList = res['summaryReport'];
        } else {
          this.detailList = res['summaryReport'];
        }

        this.loadReports(++this.pageNumber);
      }
    });
  }

  loadReports(pageNumber) {
    if (pageNumber > Math.floor(this.totalItemsCount / this.pageSize)) {
      this.isLoading = false;
      this.totGrossMarginPer = this.totGrossMargin / Math.abs(this.totSalesAmount) * 100;
    } else {
      this.apiService.getall(`sndReports/getCustomerSales?isSummary=${this.isSummary}&type=${this.type}&custCode=${this.codeControl.value}&from=${this.utilService.getCommonDate(this.dateFrom)}&to=${this.utilService.getCommonDate(this.dateTo)}&pageSize=${this.pageSize}&pageNumber=${pageNumber}`).subscribe(res => {
        if (this.isSummary) {
          this.summaryList = this.summaryList.concat(res['summaryReport']);
        } else {
          this.detailList = this.detailList.concat(res['summaryReport']);
        }

        this.totAmount += res['totAmount'];
        this.totDiscount += res['totDiscount'];
        this.totNetAmountBT += res['totNetAmountBT'];
        this.totSalesAmount += res['totSalesAmount'];
        this.totTaxAmount += res['totTaxAmount'];
        this.totCost += res['totCost'];
        this.totGrossMargin += res['totGrossMargin'];
        this.loadReports(++this.pageNumber);
      });
    }
  }

  openPrint() {
    const printContent = document.getElementById("printcontainer");
    const WindowPrt = window.open('', '', 'left=0,top=0,width=2000,height=1000,toolbar=0,scrollbars=0,status=0');
    setTimeout(() => {
      WindowPrt.document.write(printContent.innerHTML);
      WindowPrt.document.close();
      WindowPrt.focus();
      WindowPrt.print();
      WindowPrt.close();
    }, 50);
  }

}

